import React ,{useState} from 'react'

export default function Calculer_frais() { 
    const [montant, setMontant] = useState(0);
    const [frais , setFrais] = useState({
        droit_enregistrement: 0,
        conservation: 0, 
        frais_dossier: 0 , 
        horaire_notaire: 0 , 
        tva : 0
    });
    const handleSubmit = (e) => { 
        console.log(montant);
        e.preventDefault();  
        if (montant > 0) {
            const droit_enregistrement = montant * 0.04;
            const conservation = montant * 0.015 + 200 ;
            const frais_dossier = 1500;  
            let horaire_notaire ;
            switch (montant) {
                case montant <300000:
                     horaire_notaire = 40000 ;
                    break;
                case montant >= 300000 && montant < 1000000:
                     horaire_notaire = montant *0.015 ;
                    break;
                case montant >= 1000000 && montant < 5000000:
                     horaire_notaire = montant * 0.0125;
                    break; 
                case montant >= 5000000 && montant < 10000000:
                     horaire_notaire = montant * 0.075 ;
                    break;   
                case montant >=10000000 :
                    horaire_notaire = montant * 0.05 ;   
                break;
                default:
                    break;
            }
            const tva = horaire_notaire * 0.01;
        setFrais({  droit_enregistrement, conservation, frais_dossier , horaire_notaire , tva}); 
        console.log(frais);
        }
    }
  return (
    <div>
        <form onSubmit={handleSubmit}>
            <label>Montant de vente du bien:</label>
            <input type="number" value={montant} onChange={(e) => setMontant(e.target.value)} />
            <button type="submit">Calculer</button>
        </form>
    </div>
  )
}
