import axios from "axios" 
import { getall } from "../../apis/endpoints";
const dosierFetching = async () => {
  try {
    const response = await axios.get(`${process.env.REACT_APP_BACKEND_URL}${getall}`);
    console.log(response.data);  
    response.data.map((frais) => (
        {
            id : frais.id_dossier,
            Driots_en : frais.Driots_en,
            Consevation : frais.Consevation,
            Date : frais.Date,
            total : frais.total
        } 
    ))
    return response.data.map((frais) => (
        {
            id : frais.id_dossier,
            Driots_en : frais.Driots_en,
            Consevation : frais.Consevation,
            Date : frais.Date,
            total : frais.total
        } 
    ))
} catch (error) {
    console.error("Erreur lors de la récupération des dossiers :", error.response?.data || error.message);
    return []
}
}; 
        // {
        //     id: 3000000,
        //     Driots_en: 120000,
        //     Consevation: 45200,
        //     Date: new Date().toISOString().split('T')[0],
        //     total: 207950
        // }
const initialeState={
    ListeSimultaion : [
      dosierFetching()
    ]
} 

const FraisReducer = (state = initialeState, action) => {
    switch (action.type) {
        case "AJOUTER":
            return {
                ListeSimultaion: [...state.ListeSimultaion, action.payload]
            } 
        case "VIDER":
            return {
                ListeSimultaion: []
            }
        default:
            return state;
    }
} 

export default FraisReducer
